import React, { useRef, useEffect } from 'react'
import {Avatar, Button, CssBaseline, TextField, FormControlLabel, Link, Grid, Typography, Container, Box} from '@mui/material';


export default function LoginWithGoogle({ onGoogleSignIn = () => {}, text = 'signin_with' }) {
  const googleSignInButton = useRef(null);

    const handleLoginWithGoogle = () => {
      //Redirect SpringBoot backend Oauth endpoint
      window.location.href = `${process.env.API_BASE_URL}/oauth2/authorization/google?redirect_uri=${process.env.OAUTH2_REDIRECT_URI}`;
    };

  useEffect(() => {
    useScript('https://accounts.google.com/gsi/client', () => {
      //Initializing Google Sign-In
      window.google.accounts.id.initialize({
        client_id: process.env.REACT_APP_GOOGLE_CLIENT_ID,
        callback: onGoogleSignIn,
      });

      //Render Google Sign-In button (https://developers.google.com/identity/gsi/web/reference/js-reference#google.accounts.id.renderButton)
      window.google.accounts.id.renderButton(
        googleSignInButton.current,
        { theme: 'filled_blue', size: 'large', text, width: '250' }, 
      );
    });
  }, [onGoogleSignIn, text]);

  return (
    <div>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box sx={{ marginTop: 8, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <Typography component="h1" variant="h5">
            Sign in
          </Typography>
          <Box sx={{ mt: 3 }}>
            <Button fullWidth sx={{ backgroundColor: '#A2B575', color: 'white' }} onClick={handleLoginWithGoogle}>
              Sign in with Google
            </Button>
          </Box>
          <div ref={googleSignInButton}></div>
        </Box>
      </Container>
    </div>
  );
}
