import React, { useRef, useEffect } from 'react'
import {Avatar, Button, CssBaseline, TextField, FormControlLabel, Link, Grid, Typography, Container, Box} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import {GoogleSignIn} from './OauthRedirect';
import { postToken } from '../../api/postToken';

export default function LoginWithGoogle({isLogin, setIsLogin }) {
  const navigate = useNavigate();

    const googleSignInUrl = `${process.env.API_BASE_URL}/oauth2/authorization/google?redirect_uri=${process.env.OAUTH2_REDIRECT_URI}`;

    const handleLoginWithGoogle = () => {
      //Redirect SpringBoot backend Oauth endpoint
      window.location.href = googleSignInUrl;
    };

    //Callback function for Google Sign-In
  const onGoogleSignIn = async (response) => {
    const { credential } = response;
    const result = await postToken(credential, setIsLogin);
    setIsLogin(result);
  };

  useEffect(() => {
    //If already logged in, navigate to the dashboard
    if (isLogin) {
      navigate('/dashboard'); 
    }
  }, [isLogin, navigate]);

  return (
    <>
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <Box sx={{ marginTop: 15, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Typography component="h1" variant="h5">
          Sign in with Google
        </Typography>
        <Box sx={{ mt: 3 }}>
          <TextField
            fullWidth
            variant="outlined"
            value={googleSignInUrl}
            InputProps={{
              readOnly: true,
            }}
          />
        </Box>
        <Box sx={{ mt: 2 }}>
          <GoogleSignIn onGoogleSignIn={onGoogleSignIn} text="Sign in with Google"/>
          <Button
            onClick={handleLoginWithGoogle}
            style={{ zIndex: 2, backgroundColor: '#A2B575', color: 'white', padding: '8px 16px', border: 'none', cursor: 'pointer' }}
          >
            Sign in
          </Button>
        </Box>
      </Box>
    </Container>
  </>
);
}
